    private void identicalBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_identicalBtnActionPerformed
        String input1 = (identicalTF1.getText());
        String input2 = (identicalTF2.getText());
        boolean output = input1.equals(input2);
        if (output == true) {
            String yes = "Yes";
            identicalLbl.setText(yes);
        } else {
            identicalLbl.setText("No");
        }

    }//GEN-LAST:event_identicalBtnActionPerformed

