    private void fourthCharBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fourthCharBtnActionPerformed
        String input = (fourthCharTF.getText());
        char fourth = (input.charAt(3));
        String change = Character.toString(fourth);
        fourthCharLbl.setText(change);
    }//GEN-LAST:event_fourthCharBtnActionPerformed

