    private void locateBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_locateBtnActionPerformed
        String input = locateTF1.getText();
        String locate = locateTF2.getText();
        int output = input.indexOf(locate);
        String location = String.valueOf(output);
        locateLbl.setText("The first " + locate + " is at position " + location);
    }//GEN-LAST:event_locateBtnActionPerformed

