    private void displayLocationBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_displayLocationBtnActionPerformed
        String locate = displayLocationTF.getText();
        char location4 = locate.charAt(4);
        char location5 = locate.charAt(5);
        char location6 = locate.charAt(6);
        String output=String.valueOf(location4);
        String output1=String.valueOf(location5);
        String output2=String.valueOf(location6);
        displayLocationLbl.setText(output +","+ output1 +","+ output2);
    }//GEN-LAST:event_displayLocationBtnActionPerformed

